local self = {}Houses.Client = Houses.MakeConstructor (self)function self:ctor (steamID, name, ply)	Houses.EventProvider (self)		self.SteamID = steamID	self.Name = name	self.Player = ply	self.Server = nil		self.Online = falseendfunction self:GetName ()	return self.Nameendfunction self:GetPlayer ()	return self.Playerendfunction self:GetServer ()	return self.Serverendfunction self:GetSteamID ()	return self.SteamIDendfunction self:IsOnline ()	if self.Player and self.Player:IsValid () then		return true	end	return self.Onlineend-- internalsfunction self:OnPlayerConnected (server, ply)	self.Player = ply	self.Server = server	self.Name = ply:Name ()	self:SetOnline (true)endfunction self:OnPlayerDisconnected ()	self.Player = nil	self.Server = nil	self:SetOnline (false)endfunction self:SetOnline (online)	self.Online = onlineend